import React, { Component } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';

class ProposalSubmission extends Component {
  state = {
    inputs: {
      name: '',
      description: '',
      c_type: '',

    }
  }

  handleInputChange = e => {
    this.setState({
      inputs: {
        ...this.state.inputs,
        [e.target.name]: e.target.value
      }
    });
  }

  handleSubmit = e => {
    e.preventDefault();
    console.log(this.state.inputs)
    axios.post('/challenges', {
      headers: {},
      body: this.state.inputs
    })
      .then(res => res.json())
      .catch(error => {
        console.log(error);
      });
    // console.log('PING PING')
    // this.props.history.push(`/${this.state.c_type}`);
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input onChange={this.handleInputChange} type="text" placeholder="name" name="name" />
          <input onChange={this.handleInputChange} type="text" placeholder="description" name="description" />
          <select onChange={this.handleInputChange} name="c_type">
            <option />
            <option value="begginer">Begginer</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

ProposalSubmission.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func.isRequired
  }).isRequired
};

export default ProposalSubmission;
